import React from 'react';
import { capitalizeFirstLetter } from '../../utils/helpers';

function Nav(props) {

  const [ currentPage, setCurrentPage ] = useState('Portfolio');
  return (
    <header className="flex-row px-1">
      <h2>
        <a data-testid="link" onClick={()=> setCurrentPage('Portfolio')}>
        Devin Pask Portfolio
        </a>
      </h2>
      <nav>
        <ul className="flex-row">
          <li className="mx-2">
            <a data-testid="about" onClick={()=> setCurrentPage('About')}>
              About Me
            </a>
          </li>
          <li className="mx-2">
            <a data-testid="portfoio" onClick={()=> setCurrentPage('Portfolio')}>
              Portfolio
            </a>
          </li>
          <li className="mx-2">
            <a data-testid="resume" onClick={()=> setCurrentPage('Resume')}>
              Resume
            </a>
          </li>
          <li className="mx-2">
          <a data-testid="contact" onClick={()=> setCurrentPage('Contact')}>
              Contact
            </a>
          </li>
          {categories.map((category) => (
            <li
              className={`mx-1 ${
                currentCategory.name === category.name && 'navActive'
                }`}
              key={category.name}
            >
              <span
                onClick={() => {
                  setCurrentCategory(category)
                }}
              >
                {capitalizeFirstLetter(category.name)}
              </span>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}

export default Nav;
